mapper.update:
update category
set DEL_FLAG = '1'
where CATEGORY_ID = #{categoryId,jdbcType=VARCHAR}

mapper.insert:
insert into category (CATEGORY_ID, CTITLE, REMARK,
  DATETIME, COVER, USER_ID, DEL_FLAG
  )
values (#{categoryId,jdbcType=VARCHAR}, #{ctitle,jdbcType=VARCHAR}, #{remark,jdbcType=VARCHAR},
  #{datetime,jdbcType=VARCHAR}, #{cover,jdbcType=VARCHAR}, #{userId,jdbcType=VARCHAR}, '0'
  )
